import React, { useState, useEffect } from 'react';
import './App.css';
import LinkButton from './LinkButton';

function App() {
  const [theme, setTheme] = useState('light'); // Default to light theme
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate loading delay
    setTimeout(() => setLoading(false), 3000);
  }, []);

  const toggleTheme = () => {
    setTheme(theme === 'dark' ? 'light' : 'dark');
  };

  if (loading) {
    return (
      <div className="loading-screen">
        <div className="loading-logo">
          <img
            src="https://p16-sign-va.tiktokcdn.com/tos-maliva-avt-0068/a8dd891f3abc6a41cd4fab210a4b229d~c5_1080x1080.jpeg?lk3s=a5d48078&nonce=10410&refresh_token=df0a7d7f318bd5317dd223a9f88d4f7a&x-expires=1739239200&x-signature=XJ93%2FsKOfPK2g6PDzw8r%2BjoNBCI%3D&shp=a5d48078&shcp=81f88b70"
            alt="Vinsmoke Logo"
          />
        </div>
        <div className="loading-text">Loading...</div>
      </div>
    );
  }

  return (
    <div className={`App ${theme}`}>
      <header className="header">
        <h1>Vinsmoke</h1>
        <button className="theme-toggle" onClick={toggleTheme}>
          {theme === 'dark' ? 'ðŸŒž' : 'ðŸŒ™'}
        </button>
      </header>
      <div className="logo-container">
        <img
          src="https://p16-sign-va.tiktokcdn.com/tos-maliva-avt-0068/a8dd891f3abc6a41cd4fab210a4b229d~c5_1080x1080.jpeg?lk3s=a5d48078&nonce=10410&refresh_token=df0a7d7f318bd5317dd223a9f88d4f7a&x-expires=1739239200&x-signature=XJ93%2FsKOfPK2g6PDzw8r%2BjoNBCI%3D&shp=a5d48078&shcp=81f88b70"
          alt="Vinsmoke Logo"
          className="logo"
        />
      </div>
      <div className="links-container">
        <LinkButton
          url="https://discord.gg/yourlink"
          text="Join My Discord"
          icon="discord"
        />
        <LinkButton
          url="https://instagram.com/yourlink"
          text="Follow on Instagram"
          icon="instagram"
        />
        <LinkButton
          url="https://twitch.tv/yourlink"
          text="Watch on Twitch"
          icon="twitch"
        />
        <LinkButton
          url="https://youtube.com/yourlink"
          text="Subscribe on YouTube"
          icon="youtube"
        />
        <LinkButton
          url="https://twitter.com/yourlink"
          text="Follow on Twitter"
          icon="twitter"
        />
      </div>
      <footer className="footer">
        <p>&copy; 2025 Vinsmoke - All Rights Reserved</p>
      </footer>
    </div>
  );
}

export default App;
