import React, { useState, useEffect } from 'react';
import './App.css';
import LinkButton from './LinkButton';

function App() {
  const [theme, setTheme] = useState('light');
  const [loading, setLoading] = useState(true);
  const [currentTag, setCurrentTag] = useState(0);

  const tags = [
    "🎮 Pro Gamer Mode Activated!",
    "🔥 Streaming Now - Join the Fun!",
    "💥 FPS God in the Making!",
    "🚀 Adventure Awaits - Let's Go!",
    "🏆 Winning is a Habit!",
    "🌟 Dream Big, Play Hard!",
  ];

  useEffect(() => {
    setTimeout(() => setLoading(false), 3000);

    const tagInterval = setInterval(() => {
      setCurrentTag((prev) => (prev + 1) % tags.length);
    }, 3000);

    return () => clearInterval(tagInterval);
  }, []);

  const toggleTheme = () => {
    setTheme(theme === 'dark' ? 'light' : 'dark');
  };

  if (loading) {
    return (
      <div className="loading-screen">
        <div className="loading-logo">
          <img
            src="https://p16-sign-va.tiktokcdn.com/tos-maliva-avt-0068/a8dd891f3abc6a41cd4fab210a4b229d~c5_1080x1080.jpeg"
            alt="Vinsmoke Logo"
          />
        </div>
        <div className="loading-text">Loading...</div>
      </div>
    );
  }

  return (
    <div className={`container ${theme}`}>
      <button className="theme-toggle" onClick={toggleTheme}>
        {theme === 'dark' ? '🌞' : '🌙'}
      </button>
      <header className="header">
        <div className="header-content">
          <h1>Vinsmoke</h1>
          <p>Welcome to my world! 🎮</p>
          <div className="streamer-details">
            <p>🌟 "Gaming is my passion. Let's conquer the world together!"</p>
          </div>
        </div>
        <div className="logo-section">
          <div className="logo-container">
            <img
              src="https://p16-sign-va.tiktokcdn.com/tos-maliva-avt-0068/a8dd891f3abc6a41cd4fab210a4b229d~c5_1080x1080.jpeg"
              alt="Vinsmoke Logo"
            />
          </div>
          <div className="animated-tag">{tags[currentTag]}</div>
        </div>
      </header>
      <div className="links-container">
        <LinkButton url="https://discord.gg/yourlink" text="Join My Discord" icon="discord" />
        <LinkButton url="https://instagram.com/yourlink" text="Follow on Instagram" icon="instagram" />
      </div>
    </div>
  );
}

export default App;
